import React from "react";
import "./Textarea.css";

class Textarea extends React.Component {
  state = {
    urlError: "URL is empty"
  };
  render() {
    const { urlName, handleOnChange } = this.props;
    return (
      <div>
        <textarea
          input="text"
          placeholder="Enter the URL in the category selected.."
          className="textarea"
          name="urlName"
          onChange={handleOnChange}
        >
          {urlName}
        </textarea>
        <div style={{ color: "red" }}>{this.state.urlError}</div>
      </div>
    );
  }
}
export default Textarea;
