import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import "./Nav.css";
import Dropdown from "../components/Dropdown/Dropdown";
import axios from "axios";

class Nav extends Component {
  constructor(props) {
    super(props);
    this.state = {
      urllist: []
    };
  }

  fetchUrlsBasedonCatId = categoryId => {
    axios
      .get(`/api/category/getUrls/${categoryId}`)
      .then(response => {
        this.setState({ urllist: response.data });
      })
      .catch(error => {
        console.log(error);
      });
  };

  componentDidMount = () => {
    const {
      match: { params }
    } = this.props;
    Axios.get("/api/category/all")
      .then(response => {
        this.setState({ categories: response.data });
      })
      .catch(error => {
        console.log(error);
      });
    this.fetchUrlsBasedonCatId(params.cat_id);
  };

  render() {
    const {
      match: { params }
    } = this.props;

    const { urllist } = this.state;

    return (
      <form className="form">
        <div>
          <h1>List of URLs</h1>
        </div>
        <div>
          <Dropdown
            fetchurls={true}
            fetchUrlsBasedonCatId={this.fetchUrlsBasedonCatId}
            categoryId={params.cat_id}
          />
        </div>

        <div>
          <ul>
            {urllist.map(u => {
              return <li key={u.id}>{u.urlName}</li>;
            })}
          </ul>
        </div>
      </form>
    );
  }
}
export default withRouter(Nav);
