import React from "react";
import TextField from "../../common/TextField";
import { CategoryFormStyled } from "../../Category/components/Category.styled";
import { Button } from "react-bootstrap";
import { withRouter } from "react-router-dom";
import { DropdownStyled } from "../../common/Dropdown/Dropdon.styled";
//import { DropdownStyled } from "../../common/Dropdown/Dropdon.styled";

class CreateBookmark extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      urlName: ""
    };
  }
  changeHandler = e => {
    this.setState({ [e.target.name]: e.target.value });
  };
  submitHandler = e => {
    e.preventDefault();
  };
  render() {
    const {
      fetchUrlsBasedonCatId,
      handleChange,
      categoryId,
      fetchurls,
      categories = [],
      history
    } = this.props;
    return (
      <CategoryFormStyled>
        <form onSubmit={this.submitHandler}>
          <TextField
            onChange={this.changeHandler}
            label="URL Name"
            placeholder="Enter the URL Name"
          />
          <DropdownStyled>
            <div>
              <select
                onChange={e => {
                  handleChange(e);
                  if (fetchurls) {
                    fetchUrlsBasedonCatId(categoryId);
                    history.push(`/urls/${categoryId}`);
                  }
                }}
              >
                <DropdownStyled>
                  <option>Select Category</option>
                  {categories.map((category, i) => {
                    return (
                      <option
                        value={category.categoryId}
                        key={category.categoryId}
                      >
                        {category.categoryName}
                      </option>
                    );
                  })}
                </DropdownStyled>
              </select>
            </div>
          </DropdownStyled>

          <Button type="submit">Create</Button>
        </form>
      </CategoryFormStyled>
    );
  }
}
export default withRouter(CreateBookmark);
