import React, { Component } from "react";
import Toolbar from "./components/Toolbar/Toolbar";
import SideDrawer from "./components/Toolbar/SideDrawer/SideDrawer";
import Backdrop from "./components/Backdrop/Backdrop";
import Listofurl from "./SecondPage/Listofurl";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import CategoryList from "./SecondPage/CategoryList";
import CatCreate from "./SecondPage/CatCreate";
import HomePage from "./SecondPage/HomePage";

class App extends Component {
  state = {
    SideDrawerOpen: false
  };
  drawerToggleClickHandler = () => {
    this.setState(prevState => {
      return { SideDrawerOpen: !prevState.SideDrawerOpen };
    });
  };
  backdropClickHandler = () => {
    this.setState({ SideDrawerOpen: false });
  };
  render() {
    let sideDrawer;
    let backdrop;

    if (this.state.SideDrawerOpen) {
      sideDrawer = <SideDrawer />;
      backdrop = <Backdrop click={this.backdropClickHandler} />;
    }
    return (
      <Router>
        <div style={{ height: "100%" }}>
          <Toolbar drawerClickHandler={this.drawerToggleClickHandler} />
          {sideDrawer}
          {backdrop}
          <div style={{ marginTop: 60 }}>
            <Switch>
              <Route path="/" exact component={() => "Hello"} />
              {/*<Route path="/urls" exact component={Listofurl} />
              <Route path="/urls/:cat_id" exact component={Listofurl} />
              <Route path="/category-list" exact component={CategoryList} />
              <Route path="/cat-create" exact component={CatCreate} /> */}
            </Switch>
          </div>
          <main style={{ marginTop: "64px" }}></main>
        </div>
      </Router>
    );
  }
}

export default App;
