import React from "react";
import { TextareaStyled } from "./Textarea.styled";
export default function Textarea({
  text = "",
  size = "small",
  placeholder,
  handleChange
}) {
  return (
    <TextareaStyled
      size={size}
      placeholder={placeholder}
      onChange={handleChange}
    >
      {text}
    </TextareaStyled>
  );
}
