import React from "react";
import TextField from "../../common/TextField";
import { CategoryFormStyled } from "../../Category/components/Category.styled";
import { withRouter } from "react-router-dom";
import { DropdownStyled } from "../../common/Dropdown/Dropdown.styled";
import { AnchorLinkStyled } from "../../common/AnchorLink/AnchorLink.styled";
import { ButtonStyled } from "../../common/Button/Button.styled";
import { Link } from "react-router-dom";
import Axios from "axios";

class CreateBookmark extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      urlName: "",
      selectedCat: "",
      categoryId: "",
      categories: []
    };
  }

  componentDidMount() {
    Axios.get("/api/categories/list")
      .then(response => {
        this.setState({ categories: response.data });
      })
      .catch(error => {
        console.log(error);
      });
  }
  handleChange = e => {
    console.log({ [e.target.name]: e.target.value });
    this.setState({ [e.target.name]: e.target.value });
  };

  submitHandler = e => {
    console.log(this.state);
    e.preventDefault();
    Axios.post(`/api/urls/${this.state.selectedCat}`, {
      urlName: this.state.urlName
    })
      .then(response => {
        console.log(response);
      })
      .catch(error => {
        console.log(error);
      });
  };

  render() {
    return (
      <CategoryFormStyled>
        <form
          onSubmit={e => {
            this.submitHandler(e);
            this.props.history.push(`/urls/${this.state.selectedCat}`);
          }}
        >
          <TextField
            onChange={e => {
              console.log(e.target.value);
              this.handleChange(e);
            }}
            name="urlName"
            label="URL Name"
            placeholder="Enter the URL Name"
          />
          <DropdownStyled>
            <div>
              <select
                onChange={e => {
                  this.setState({ selectedCat: e.target.value });
                  // if (fetchurls) {
                  //   fetchUrlsBasedonCatId(categoryId);
                  //   history.push(`/urls/${categoryId}`);
                  // }
                }}
              >
                <option>Select Category</option>
                {this.state.categories.map((category, i) => {
                  return (
                    <option
                      value={category.categoryId}
                      key={category.categoryId}
                    >
                      {category.categoryName}
                    </option>
                  );
                })}
              </select>
            </div>
          </DropdownStyled>
          <ButtonStyled
            type="button"
            buttonStyle="primary"
            onClick={this.submitHandler}
          >
            <AnchorLinkStyled>
              <Link to="/bookmark-list">Submit</Link>
            </AnchorLinkStyled>
          </ButtonStyled>
          <AnchorLinkStyled>
            <Link to="/bookmark-list"></Link>
          </AnchorLinkStyled>
        </form>
      </CategoryFormStyled>
    );
  }
}
export default withRouter(CreateBookmark);
