import styled, { css } from "styled-components";

export const ButtonStyled = styled.button`
  height: 50px;
  width: 100px;
  align-content: center;
  color: aliceblue;
  ${props =>
    props.primary &&
    css`
      background: white;
      color: palevioletred;
    `}

  ${props =>
    props.danger &&
    css`
      background: red;
      color: white;
    `}
`;
