import React from "react";
import TextField from "../../common/TextField";
import { CategoryFormStyled } from "../../Category/components/Category.styled";
import { withRouter } from "react-router-dom";
import { DropdownStyled } from "../../common/Dropdown/Dropdown.styled";
import { AnchorLinkStyled } from "../../common/AnchorLink/AnchorLink.styled";
import { ButtonStyled } from "../../common/Button/Button.styled";
import { Link } from "react-router-dom";
import Axios from "axios";

class CreateBookmark extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      urlName: "",
      selectedCat: ""
    };
  }
  handleChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };
  submitHandler = e => {
    e.preventDefault();
    Axios.post(`/api/urls/${categoryId}`, {
      urlName: this.state.urlName
    })
      .then(response => {
        console.log(response);
      })
      .catch(error => {
        console.log(error);
      });
  };
  render() {
    const {
      fetchUrlsBasedonCatId,
      handleChange,
      categoryId,
      fetchurls,
      categories = [],
      history
    } = this.props;
    return (
      <CategoryFormStyled>
        <form onSubmit={this.submitHandler}>
          <TextField
            onChange={this.handleChange}
            name="urlName"
            label="URL Name"
            placeholder="Enter the URL Name"
          />
          <DropdownStyled>
            <div>
              <select
                onChange={e => {
                  handleChange(e);
                  if (fetchurls) {
                    fetchUrlsBasedonCatId(categoryId);
                    history.push(`/urls/${categoryId}`);
                  }
                }}
              >
                <option>Select Category</option>
                {categories.map((category, i) => {
                  return (
                    <option
                      value={category.categoryId}
                      key={category.categoryId}
                    >
                      {category.categoryName}
                    </option>
                  );
                })}
              </select>
            </div>
          </DropdownStyled>
          <ButtonStyled
            type="button"
            buttonStyle="primary"
            onClick={this.submitHandler}
          >
            <AnchorLinkStyled>
              <Link to="/bookmark-list">Submit</Link>
            </AnchorLinkStyled>
          </ButtonStyled>
          <AnchorLinkStyled>
            <Link to="/bookmark-list"></Link>
          </AnchorLinkStyled>
        </form>
      </CategoryFormStyled>
    );
  }
}
export default withRouter(CreateBookmark);
