import styled, { css } from "styled-components";
export const TextareaStyled = styled.textarea`
  width: 400px;
  height: 23px;
  margin-left: 500px;
  margin-top: 200px;
  font-size: small;

  ${props =>
    props.buttonType === "small" &&
    css`
      height: 50px;
    `}

  ${props =>
    props.buttonType === "large" &&
    css`
      height: 100px;
    `}
`;
