import React from "react";
import "./Textarea.css";

class Textarea extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      error: ""
    };
  }
  handleSubmit = e => {
    e.preventDefault();
    const isValid = this.validate();
    if (isValid) {
      console.log(this.state);
    }
  };
  render() {
    const { urlName, handleOnChange } = this.props;
    return (
      <div>
        <form
          onSubmit={e => {
            this.handleSubmit(e);
          }}
        >
          <textarea
            input="text"
            placeholder="Enter the URL in the category selected.."
            className="textarea"
            name="urlName"
            onChange={handleOnChange}
          >
            {urlName}
          </textarea>
          {this.state.error ? (
            <div className="error">{this.state.error}</div>
          ) : null}
        </form>
      </div>
    );
  }
}
export default Textarea;
