import React from "react";
import "./Textarea.css";
import axios from "axios";
class Textarea extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      urlName: ""
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange = e => {
    this.setState({ value: e.target.value });
  };

  handleSubmit = e => {
    alert("The URL is searched: " + this.state.value);
    e.preventDefault();
    console.log(this.state);
    axios
      .post(`/api/urls/createUrl/${""}`, this.state)
      .then(response => {
        console.log(response);
      })
      .catch(error => {
        console.log(error);
      });
  };

  render() {
    const { urlName } = this.state;
    return (
      <div>
        <textarea
          input="text"
          placeholder="Enter the URL in the category selected.."
          className="textarea"
          name="urlName"
          value={urlName}
        />
      </div>
    );
  }
}
export default Textarea;
