import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import "./Nav.css";
import Dropdown from "../components/Dropdown/Dropdown";
import axios from "axios";
import Axios from "axios";

class Nav extends Component {
  constructor(props) {
    super(props);
    this.state = {
      urllist: [],
      categories: [],
      selectedCat: ""
    };
  }

  fetchUrlsBasedonCatId = categoryId => {
    axios
      .get(`/api/category/getUrls/${categoryId}`)
      .then(response => {
        this.setState({ urllist: response.data });
      })
      .catch(error => {
        console.log(error);
      });
  };

  handleCatChange = e => {
    this.setState({
      selectedCat: e.target.value
    });
  };

  componentDidMount = () => {
    const {
      match: { params }
    } = this.props;
    Axios.get("/api/category/all")
      .then(response => {
        this.setState({ categories: response.data });
      })
      .catch(error => {
        console.log(error);
      });
    this.fetchUrlsBasedonCatId(params.cat_id);
  };

  render() {
    const {
      match: { params }
    } = this.props;

    const { urllist, categories = [], selectedCat } = this.state;

    return (
      <form className="form">
        <div>
          <h1>List of URLs</h1>
        </div>
        <div>
          <Dropdown
            handleChange={this.handleCatChange}
            categories={categories}
            fetchurls={true}
            fetchUrlsBasedonCatId={this.fetchUrlsBasedonCatId}
            categoryId={selectedCat}
          />
        </div>

        <div>
          <li>
            <hr />
            <button style={{ color: "red" }}>Delete</button>
            <button style={{ color: "blue" }}>Update</button>
            {urllist.map(u => {
              return <li key={u.id}>{u.urlName}</li>;
            })}
            <hr />
          </li>
        </div>
      </form>
    );
  }
}
export default withRouter(Nav);
