import "./Shop.css";
import { Link } from "react-router-dom";
import axios from "axios";
import React, { Component } from "react";

class Shop extends Component {
  constructor(props) {
    super(props);

    this.state = {
      categories: [],
      selectedCat: ""
    };
  }
  componentDidMount() {
    axios
      .get("/api/category/all")
      .then(response => {
        this.setState({ categories: response.data });
      })
      .catch(error => {
        console.log(error);
