  render() {
    const { categories } = this.state;
    return (
      <div>
        <form className="shop">
          <div>
            <h1>List of Categories</h1>
            {categories.length
              ? categories.map(categories => (
                  <div key={categories.id}>
                    <hr />

                    <button style={{ color: "red" }} onClick={this.deleteData}>
                      Delete
                    </button>
                    <button style={{ color: "blue" }}>Update</button>

                    <li>{categories.categoryName}</li>
                  </div>
                ))
              : null}
          </div>
          <div>
            <Link to="/about">
              <button className="click">+</button>
            </Link>
          </div>
        </form>
      </div>
    );
  }
}
export default Shop;
