import React from "react";
import "./Textarea.css";

class Textarea extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      error: ""
    };
  }
  validate = () => {
    let error = "";
    if (!this.state.urlName.length) {
      error = "Empty URL!";
    }
    if (error) {
      this.setState({ error });
      return false;
    }
    return true;
  };
  handleSubmit = e => {
    e.preventDefault();
    const isValid = this.validate();
    if (isValid) {
      console.log(this.state);
    }
  };
  render() {
    const { urlName, handleOnChange } = this.props;
    return (
      <div>
        <h2 style={{ color: "#521751" }}>CREATION OF URLS</h2>
        <form
          onSubmit={e => {
            this.handleSubmit(e);
          }}
        >
          <textarea
            input="text"
            rows="2"
            placeholder="Enter the URL in the category selected.."
            className="textarea"
            name="urlName"
            onChange={handleOnChange}
          >
            {urlName}
          </textarea>

          {this.state.error ? (
            <div className="error">{this.state.error}</div>
          ) : null}
        </form>
      </div>
    );
  }
}
export default Textarea;
