import styled, { css } from "styled-components";

export const ButtonStyled = styled.button`
  height: 50px;
  width: 100px;
  align-content: center;
  color: black;
  ${props =>
    props.buttonType === "primary" &&
    css`
      background: white;
      color: palevioletred;
    `}

  ${props =>
    props.buttonType === "danger" &&
    css`
      background: red;
      color: white;
    `}
`;
