import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import "./Nav.css";
import Dropdown from "../components/Dropdown/Dropdown";
import axios from "axios";

class Nav extends Component {
  constructor(props) {
    super(props);
    this.state = {
      urllist: []
    };
  }

  fetchUrlsBasedonCatId = categoryId => {
    axios
      .post(`/api/urls/createUrl/${categoryId}`)
      .then(response => {
        this.setState({ urllist: response.data });
      })
      .catch(error => {
        console.log(error);
      });
  };

  componentDidMount = () => {
    const {
      match: { params }
    } = this.props;

    this.fetchUrlsBasedonCatId(params.categoryId);
  };

  render() {
    const {
      match: { params }
    } = this.props;

    return (
      <form className="form">
        <div>
          <h1>List of URLs</h1>
        </div>
        <div>
          <Dropdown
            fetchurls={true}
            fetchUrlsBasedonCatId={this.fetchUrlsBasedonCatId}
            categoryId={params.cat_id}
          />
        </div>
      </form>
    );
  }
}
export default withRouter(Nav);
