import React from "react";
import TextArea from "../../common/Textarea";
import TextField from "../../common/TextField";
import { Button } from "react-bootstrap";
import { CategoryFormStyled } from "./Category.styled";

export default class CreateCategory extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      categoryName: "",
      categoryDesc: "asas"
    };
  }

  render() {
    return (
      <CategoryFormStyled>
        <form
          onSubmit={e => {
            console.log("asas");
            e.preventDefault();
            //api call with the state values
          }}
        >
          <TextField
            handleChange={e => {
              this.setState({ categoryName: e.target.value });
            }}
            label="Ctaegory Name"
          />
          <TextArea
            placeholder="Enter the description"
            handleChange={e => {
              this.setState({ categoryDesc: e.target.value });
            }}
            label="Ctaegory Description"
          />

          <Button type="submit">Submit</Button>
        </form>
      </CategoryFormStyled>
    );
  }
}
