import React from "react";
import "./Dropdown.css";
import axios from "axios";
import { withRouter } from "react-router-dom";

class Dropdown extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      categories: [],
      selectedCat: ""
    };
  }
  componentDidMount() {
    axios
      .get("/api/category/all")
      .then(response => {
        this.setState({ categories: response.data });
      })
      .catch(error => {
        console.log(error);
      });
  }

  render() {
    const { categories, selectedCat } = this.state;
    console.log(selectedCat);
    return (
      <form>
        <nav>
          <select
            onChange={(e, value) => {
              console.log(value);
              this.setState({
                selectedCat: value
              });
              this.props.history.push(`/$value}`);
            }}
            className="box"
          >
            {categories.map((category, i) => {
              return (
                <option value={category.categoryId} key={i} value="">
                  {category.categoryName}
                </option>
              );
            })}
          </select>
        </nav>
        <div>
          <input type="submit" value="Submit" className="style" />
        </div>
      </form>
    );
  }
}
export default withRouter(Dropdown);
