import React from "react";
import "./Dropdown.css";
import axios from "axios";
import { withRouter } from "react-router-dom";

class Dropdown extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      categories: [],
      selectedCat: ""
    };
  }
  componentDidMount() {
    axios
      .get("/api/category/all")
      .then(response => {
        this.setState({ categories: response.data });
      })
      .catch(error => {
        console.log(error);
      });
  }

  render() {
    const { categories = [], fetchurls, selectedCat } = this.state;

    const { fetchUrlsBasedonCatId } = this.props;

    return (
      <form
        onSubmit={e => {
          e.preventDefault();
          this.props.history.push(`/urls/${selectedCat}`);
        }}
      >
        <nav>
          <select
            onChange={e => {
              this.setState({
                selectedCat: e.target.value
              });
              // use a flag to call api to fetch urls based on cat id
              if (fetchurls) {
                fetchUrlsBasedonCatId();
              }
            }}
            className="box"
          >
            <option>Select Category</option>
            {categories.map((category, i) => {
              return (
                <option value={category.categoryId} key={category.categoryId}>
                  {category.categoryName}
                </option>
              );
            })}
          </select>
        </nav>
        <div>
          <input type="submit" value="Submit" className="style" />
        </div>
      </form>
    );
  }
}
export default withRouter(Dropdown);
