import React, { Component } from "react";
import "./About.css";
import axios from "axios";

class About extends Component {
  constructor(props) {
    super(props);

    this.state = {
      categoryName: "",
      categoryDescription: "",
      urlError: "",
      descError: ""
    };
  }
  changeHandler = e => {
    this.setState({ [e.target.name]: e.target.value });
  };
  validate = () => {
    let urlError = "";
    let descError = "";
    if (!this.state.categoryDescription.length) {
      descError = "Description cannot be blank!";
    }
    if (!this.state.categoryName.length) {
      urlError = "Category cannot be blank!";
    }
    if (urlError || descError) {
      this.setState({ urlError, descError });
      return false;
    }
    return true;
  };
  submitHandler = e => {
    e.preventDefault();
    const isValid = this.validate();
    if (isValid) {
      axios
        .post("/api/category/create", this.state)
        .then(response => {
          console.log(response);
        })
        .catch(error => {
          console.log(error);
        });
      console.log(this.state);
    } else {
      alert("Cannot be created");
    }
  };
  render() {
    const { categoryName, categoryDescription } = this.state;
    return (
      <form onSubmit={this.submitHandler}>
        <div>
          <textarea
            label="Name"
            input="text"
            placeholder="Enter the category name.. "
            className="textarea1"
            name="categoryName"
            value={categoryName}
            onChange={this.changeHandler}
          />
          {this.state.urlError ? (
            <div className="error">{this.state.urlError}</div>
          ) : null}
        </div>
        <div>
          <textarea
            label="Description"
            input="text"
            placeholder="Enter the category description.."
            className="textarea2"
            name="categoryDescription"
            value={categoryDescription}
            onChange={this.changeHandler}
          />
          {this.state.descError ? (
            <div className="error1">{this.state.descError}</div>
          ) : null}
        </div>
        <div>
          <button className="button">Create</button>
        </div>
      </form>
    );
  }
}

export default About;
<Form onSubmit={this.submitHandler}>
  <Form.Label>Category Name</Form.Label>
  <Form.Control type="text" placeholder="Enter the category name..." />
  <Form.Text className="text-muted">
    Category name cannot be blank
    <Form.name>"categoryDescription"</Form.name>
    <Form.value>{categoryDescription}</Form.value>
    <Form.onChange>{this.changeHandler}</Form.onChange>
  </Form.Text>

  <Form.Label>Category Description</Form.Label>
  <Form.Control type="text" placeholder="Enter the Category Description.. " />

  <Form.Group controlId="formBasicCheckbox">
    <Form.Check type="checkbox" label="Check me out" />
  </Form.Group>
  <Button variant="primary" type="submit">
    Create
  </Button>
</Form>;
