import React from "react";
import TextField from "../../common/TextField";
import { CategoryFormStyled } from "../../Category/components/Category.styled";
import { Button } from "react-bootstrap";
import { withRouter } from "react-router-dom";
import { DropdownStyled } from "../../common/Dropdown/Dropdon.styled";
//import { DropdownStyled } from "../../common/Dropdown/Dropdon.styled";

class CreateBookmark extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      urlName: ""
    };
  }
  changeHandler = e => {
    this.setState({ [e.target.name]: e.target.value });
  };
  submitHandler = e => {
    e.preventDefault();
  };
  render() {
    return (
      <CategoryFormStyled>
        <form onSubmit={this.submitHandler}>
          <TextField
            onChange={this.changeHandler}
            label="URL Name"
            placeholder="Enter the URL Name"
          />
          <DropdownStyled>
            <div>
              <select>
                <option value="grapefruit">Grapefruit</option>
                <option value="lime">Lime</option>
                <option selected value="coconut">
                  Coconut
                </option>
                <option value="mango">Mango</option>
              </select>
            </div>
          </DropdownStyled>

          <Button type="submit">Create</Button>
        </form>
      </CategoryFormStyled>
    );
  }
}
export default withRouter(CreateBookmark);
