import "./Shop.css";
import { Link } from "react-router-dom";
import axios from "axios";
import React, { Component } from "react";

class Shop extends Component {
  constructor(props) {
    super(props);

    this.state = {
      categories: [],
      selectedCat: ""
    };
  }
  deleteData = categoryId => {
    axios
      .delete(`/api/category/delete/${categoryId}`)
      .then(response => {
        console.log(response.data);
      })
      .catch(error => {
        console.log(error);
      });
  };
  handleChange = e => {
    this.setState({
      selectedCat: e.target.value
    });
  };
  componentDidMount = () => {
    const {
      match: { params }
    } = this.props;
    axios
      .get("/api/category/all")
      .then(response => {
        this.setState({ categories: response.data });
      })
      .catch(error => {
        console.log(error);
      });
    this.deleteData(params.cat_id);
  };

  render() {
    const { categories = [], selectedCat } = this.state;
    return (
      <div>
        <form className="shop">
          <div>
            <h1>List of Categories</h1>
            {categories.length
              ? categories.map(categories => (
                  <div key={categories.id}>
                    <hr />

                    <button
                      style={{ color: "red" }}
                      handleChange={this.handleChange}
                      onClick={this.deleteData}
                      categoryId={selectedCat}
                    >
                      Delete
                    </button>
                    <button style={{ color: "blue" }}>Update</button>

                    <li>{categories.categoryName}</li>
                  </div>
                ))
              : null}
          </div>
          <div>
            <Link to="/about">
              <button className="click">+</button>
            </Link>
          </div>
        </form>
      </div>
    );
  }
}
export default Shop;
