import React from "react";
import Textarea from "./components/Textarea/Textarea";
import Dropdown from "./components/Dropdown/Dropdown";
import Axios from "axios";
import "./Home.css";

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      urlName: "",
      selectedCat: "",
      categories: []
    };
  }
  validate = () => {
    let urlError = "";
    if (!this.state.urlName.length) {
      urlError = "Empty URL!";
    }
    if (urlError) {
      this.setState({ urlError });
      return false;
    }
    return true;
  };

  handleChange = e => {
    this.setState({ value: e.target.value });
  };

  handleSubmit = e => {
    e.preventDefault();
    const isValid = this.validate();
    if (isValid) {
      console.log(this.state);
    }

    Axios.post(`/api/urls/createUrl/${this.state.selectedCat}`, {
      urlName: this.state.urlName
    })
      .then(response => {
        console.log(response);
      })
      .catch(error => {
        console.log(error);
      });
  };

  handleCatChange = e => {
    this.setState({
      selectedCat: e.target.value
    });
  };

  componentDidMount() {
    Axios.get("/api/category/all")
      .then(response => {
        this.setState({ categories: response.data });
      })
      .catch(error => {
        console.log(error);
      });
  }

  render() {
    const { categories = [], fetchurls, selectedCat } = this.state;

    return (
      <div>
        <form
          onSubmit={e => {
            this.handleSubmit(e);
            this.props.history.push(`/urls/${selectedCat}`);
          }}
        >
          <Textarea
            handleOnChange={e => {
              this.setState({
                urlName: e.target.value
              });
            }}
            urlName={this.state.urlName}
          />
          <Dropdown
            categories={categories}
            handleChange={this.handleCatChange}
            categoryId={selectedCat}
          />
          <div>
            <button className="bb1">Submit</button>
          </div>
        </form>
      </div>
    );
  }
}

export default Home;
