import React from "react";
import TextField from "../../common/TextField";
import { CategoryFormStyled } from "../../Category/components/Category.styled";
import { Button } from "react-bootstrap";
import { withRouter } from "react-router-dom";
import { DropdownStyled } from "../../common/Dropdown/Dropdown.styled";
import { AnchorLinkStyled } from "../../common/AnchorLink/AnchorLink.styled";
import { ButtonStyled } from "../../common/Button/Button.styled";
import { Link } from "react-router-dom";

class CreateBookmark extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      urlName: ""
    };
  }
  changeHandler = e => {
    this.setState({ [e.target.name]: e.target.value });
  };
  submitHandler = e => {
    e.preventDefault();
  };
  render() {
    const {
      fetchUrlsBasedonCatId,
      handleChange,
      categoryId,
      fetchurls,
      categories = [],
      history
    } = this.props;
    return (
      <CategoryFormStyled>
        <form onSubmit={this.submitHandler}>
          <TextField
            onChange={this.changeHandler}
            label="URL Name"
            placeholder="Enter the URL Name"
          />
          <DropdownStyled>
            <div>
              <select
                onChange={e => {
                  handleChange(e);
                  if (fetchurls) {
                    fetchUrlsBasedonCatId(categoryId);
                    history.push(`/urls/${categoryId}`);
                  }
                }}
              >
                <option>Select Category</option>
                {categories.map((category, i) => {
                  return (
                    <option
                      value={category.categoryId}
                      key={category.categoryId}
                    >
                      {category.categoryName}
                    </option>
                  );
                })}
              </select>
            </div>
          </DropdownStyled>
          <ButtonStyled type="button">Submit</ButtonStyled>
          <AnchorLinkStyled>
            <Link to="/bookmark-list"></Link>
          </AnchorLinkStyled>
        </form>
      </CategoryFormStyled>
    );
  }
}
export default withRouter(CreateBookmark);
