import React, { Component } from "react";
import "./About.css";
import axios from "axios";

class About extends Component {
  constructor(props) {
    super(props);

    this.state = {
      categoryName: "",
      categoryDescription: "",
      urlError: "",
      descError: ""
    };
  }
  changeHandler = e => {
    this.setState({ [e.target.name]: e.target.value });
  };
  validate = () => {
    let urlError = "";
    let descError = "";
    if (!this.state.categoryDescription.length) {
      descError = "Empty Description";
    }
    if (!this.state.categoryName.length) {
      urlError = "Empty category!";
    }
    if (urlError || descError) {
      this.setState({ urlError, descError });
      return false;
    }
    return true;
  };
  submitHandler = e => {
    e.preventDefault();
    const isValid = this.validate();
    if (isValid) {
      console.log(this.state);
    }
    axios
      .post("/api/category/create", this.state)
      .then(response => {
        console.log(response);
      })
      .catch(error => {
        console.log(error);
      });
  };
  render() {
    const { categoryName, categoryDescription } = this.state;
    return (
      <form onSubmit={this.submitHandler}>
        <div>
          <textarea
            label="Name"
            input="text"
            placeholder="Enter the category name.. "
            className="textarea1"
            name="categoryName"
            value={categoryName}
            onChange={this.changeHandler}
          />
          {this.state.urlError ? (
            <div className="error">{this.state.urlError}</div>
          ) : null}
        </div>
        <div>
          <textarea
            label="Description"
            input="text"
            placeholder="Enter the category description.."
            className="textarea2"
            name="categoryDescription"
            value={categoryDescription}
            onChange={this.changeHandler}
          />
          {this.state.descError ? <div>{this.state.descError}</div> : null}
        </div>
        <div>
          <button className="button">Create</button>
        </div>
      </form>
    );
  }
}

export default About;
