import React from "react";
import "./Textarea.css";

class Textarea extends React.Component {
  render() {
    const { urlName, handleOnChange } = this.props;
    return (
      <div>
        <textarea
          input="text"
          placeholder="Enter the URL in the category selected.."
          className="textarea"
          name="urlName"
          onChange={handleOnChange}
        >
          {urlName}
        </textarea>
        {this.state.urlError ? (
          <div className="error">{this.state.urlError}</div>
        ) : null}
      </div>
    );
  }
}
export default Textarea;
