import React from "react";
import "./Dropdown.css";
import axios from "axios";
class Dropdown extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      categories: []
    };
  }
  componentDidMount() {
    axios
      .get("/api/category/all")
      .then(response => {
        this.setState({ categories: response.data });
      })
      .catch(error => {
        console.log(error);
      });
  }

  render() {
    const { categories } = this.state;
    return (
      <form>
        <nav>
          <select className="box">
            {categories.map(categories => (
              <option value="">
                <div>
                  <div key={categories.id}>{categories.categoryName}</div>
                </div>
              </option>
            ))}
          </select>
        </nav>
        <div>
          <input type="submit" value="Submit" className="style" />
        </div>
      </form>
    );
  }
}
export default Dropdown;
